                      label: rule!AS_GAM_CO_I18N_UT_displayLabel(
                        bundle: ri!bundle,
                        bundleKey: a!match(
                          value: null,
                          whenTrue: isnull(
                            ri!combinedConsensusResponse.consensusResponseId
                          ),
                          then: "btn_CombineX",
                          whenTrue: length(local!selectedConsensusResponseIndexes) = 0,
                          then: "btn_Separate",
                          default: "btn_UpdateX"
                        ),
                        arguments: count(local!selectedConsensusResponses)
                      ),
                      style: "SOLID",
                      color: "ACCENT",
                      loadingIndicator: true,
                      disabled: not(local!enableCombine),
                      accessibilityText: ri!responseTypeMap.responseTypeLabelSingle,
                      tooltip: if(
                        and(
                          local!enableCombine,
                          /*If selected method was Suggest combined response using AI*/
                          local!selectedMethodToCombineResponses = 1
                        ),
                        rule!AS_GAM_CO_I18N_UT_displayLabel(
                          bundle: ri!bundle,
                          bundleKey: "hlp_SuggestedResponseGeneration"
                        ),
                        null()
                      ),
                      saveInto: a!flatten(
                        {
                          if(
                            a!isNullOrEmpty(local!selectedConsensusResponseIndexes),
                            /* Handle separation */
                            {
                              if(
                                /* <= 0 means not yet saved */
                                ri!combinedConsensusResponse.consensusResponseId <= 0,
                                a!save(
                                  ri!consensusResponses,
                                  remove(
                                    ri!consensusResponses,
                                    local!indexOfGroupedConsensusResponse
                                  )
                                ),
                                a!save(
                                  ri!consensusResponses[local!indexOfGroupedConsensusResponse].isActive,
                                  false
                                )
                              ),
                              /* Handle deselected responses */
                              if(
                                a!isNotNullOrEmpty(local!deselectedResponseIndexes),
                                a!save(
                                  ri!consensusResponses[local!deselectedResponseIndexes].combinedConsensusResponseId,
                                  null
                                ),
                                {}
                              ),
                              /* Exit */
                              a!save(local!formSubmitRefresh, now()),
                              a!save(ri!combinedConsensusResponse, null)
                            },
                            /* Handle combination */
                            {
                              if(
                                /*If selected method was Suggest combined response using AI*/
                                local!selectedMethodToCombineResponses = 1,
                                {
                                  a!save(local!dismissBanner, false),
                                  a!startProcess3(
                                    processModel: cons!AS_GSS_PM_COMBINE_RESPONSES_USING_AI,
                                    processParameters: {
                                      consensusId: ri!consensusResponses.consensusId[1],
                                      formattedConsensusResponseInputs: rule!AS_GSS_BL_formatConsensusResponses_ToCombineResponsesByAI(
                                        consensusResponses: local!selectedConsensusResponses,

                                      ),
                                      useNativeAi:rule!AS_GSS_EnableOrDisableAppianAIForSummarization_wrapper()
                                    },
                                    isSynchronous: true(),
                                    onSuccess: {
                                      a!save(
                                        local!integrationResults,
                                        fv!processInfo.pv.consensusResponseAiSummary
                                      ),
                                      if(
                                        local!integrationResults.isSuccess,
                                        {
                                          a!save(
                                            ri!combinedConsensusResponse,
                                            #"urn:appian:function:v1:a:update"(
                                              ri!combinedConsensusResponse,
                                              {
                                                "response",
                                                "justification",
                                                "isOriginallyGeneratedByAi"
                                              },
                                              {
                                                index(
                                                  rule!AS_GSS_UT_deepIndex(
                                                    item: fv!processInfo.pv.combinedResponseMap,
                                                    indexArray: { "Response", "Chat Completions" }
                                                  ),
                                                  1,
                                                  null
                                                ),
                                                index(
                                                  rule!AS_GSS_UT_deepIndex(
                                                    item: fv!processInfo.pv.combinedjustificationMap,
                                                    indexArray: { "Response", "Chat Completions" }
                                                  ),
                                                  1,
                                                  null
                                                ),
                                                true()
                                              }
                                            )
                                          )
                                        },
                                        {}
                                      )
                                    }
                                  )
                                },
                                {
                                  /* manual combination of responses */
                                  a!save(
                                    ri!combinedConsensusResponse.response,
                                    joinarray(
                                      local!selectedConsensusResponses.response,
                                      char(10)
                                    )
                                  ),
                                  a!save(
                                    ri!combinedConsensusResponse.justification,
                                    joinarray(
                                      local!selectedConsensusResponses.justification,
                                      char(10)
                                    )
                                  ),
                                  a!save(
                                    ri!combinedConsensusResponse.isOriginallyGeneratedByAi,
                                    false
                                  ),
                                  if(
                                    cons!AS_GSS_TOGGLE_CONSENSUS_FORM_AI,
                                    rule!AS_GSS_MTR_SAVE_combinedConsensusResponsesWithoutConsensusAI(),
                                    {}
                                  )
                                }
                              ),
                              if(
                                not(
                                  and(local!integrationResults.isSuccess)
                                ),
                                null,
                                {
                                  /* References are combined manually in both cases */
                                  a!save(
                                    ri!combinedConsensusResponse.references,
                                    joinarray(
                                      union(
                                        local!selectedConsensusResponses.references,
                                        touniformstring({})
                                      ),
                                      char(10)
                                    )
                                  ),
                                  /* Handle new ccombined response */
                                  if(
                                    isnull(
                                      ri!combinedConsensusResponse.consensusResponseId
                                    ),
                                    {
                                      a!save(
                                        ri!combinedConsensusResponse.consensusResponseId,
                                        min(
                                          0,
                                          ri!consensusResponses.consensusResponseId
                                        ) - 1
                                      ),
                                      a!save(
                                        ri!consensusResponses,
                                        append(
                                          ri!consensusResponses,
                                          ri!combinedConsensusResponse
                                        )
                                      )
                                    },
                                    a!save(
                                      ri!consensusResponses[local!indexOfGroupedConsensusResponse],
                                      ri!combinedConsensusResponse
                                    )
                                  ),
                                  /* Handle selected responses */
                                  a!save(
                                    ri!consensusResponses[local!selectedConsensusResponseIndexes].combinedConsensusResponseId,
                                    ri!combinedConsensusResponse.consensusResponseId
                                  ),
                                  /* Handle deselected responses */
                                  if(
                                    a!isNotNullOrEmpty(local!deselectedResponseIndexes),
                                    a!save(
                                      ri!consensusResponses[local!deselectedResponseIndexes].combinedConsensusResponseId,
                                      null
                                    ),
                                    {}
                                  ),
                                  /*
                                    These variables will not update if two new combinations are created in a row
                                    or if the same combination is updated twice in a row without change
                                    because this form is hidden before the locals refresh
                                    and then the referenced vars match on reappearance
                                  */
                                  a!save(local!formSubmitRefresh, now()),
                                  /* Exit */
                                  a!save(ri!combinedConsensusResponse, null)
                                }
                              )
                            }
                          )
                        }
                      )
                    )
                  },
                  align: "END"
                )
              )
            },
            marginBelow: "NONE"
          )
        },
        divider: "ABOVE",
        marginAbove: "NONE",
        marginBelow: "NONE"
      )
    }
  )
)

