  if(
    or(
      rule!AS_CO_UT_isBlank(local!vendorAndDocumentsData.result),
      rule!AS_CO_UT_isBlank(
        rule!AS_GSS_UT_deepIndex(
          item: local!responseBody,
          indexArray: { "vendorDetails" }
