detailed_changes.sail_code_before:
a!localVariables(
  local!firstMessage: if(
    and(
      a!isNullOrEmpty(
        value: rule!AS_CO_UT_indexPath(
          cdt: ri!missingItems,
          field: "[1].parentAction",
          indexDefault: {}
        )
      ),
      a!isNullOrEmpty(
        value: rule!AS_CO_UT_indexPath(
          cdt: ri!missingItems,
          field: "[1].childAction",
          indexDefault: {}
        )
      )
    ),
    /*  Configuration for messages that has no actions eg: Review vendors for VM updates  */
    a!map(
      message: rule!AS_CO_UT_indexPath(
        cdt: ri!missingItems,
        field: "message",
        indexDefault: {}
      ),
      action: {}
    ),
    if(
      a!isNullOrEmpty(
        value: rule!AS_CO_UT_indexPath(
          cdt: ri!missingItems,
          field: "[1].parentAction",
          indexDefault: {}
        )
      ),
      /*  Configuration for messages which has child actions eg: Update Vendors  */
      a!map(
        message: rule!AS_CO_UT_indexPath(
          cdt: ri!missingItems,
          field: "[1].message.[1]",
          indexDefault: {}
        ),
        action: rule!AS_CO_UT_indexPath(
          cdt: ri!missingItems,
          field: "[1].childAction.[1]",
          indexDefault: {}
        )
      ),
      /*  Configuration for messages that has parent actions eg: Update Name, Update Description etc  */
      a!map(
        message: if(
          rule!AS_GSS_CO_UT_ArrayLength(
            array: rule!AS_CO_UT_indexPath(
              cdt: ri!missingItems,
              field: "[1].message",
              indexDefault: {}
            )
          ) > 1,
          rule!AS_CO_UT_indexPath(
            cdt: ri!missingItems,
            field: "[1].message.[1]",
            indexDefault: {}
          ),
          rule!AS_CO_UT_indexPath(
            cdt: ri!missingItems,
            field: "[1].message",
            indexDefault: {}
          )
        ),
        action: rule!AS_CO_UT_indexPath(
          cdt: ri!missingItems,
          field: "[1].parentAction",
          indexDefault: {}
        )
      )
    )
  ),
  {
    a!cardLayout(
