    value: true,
    refreshOnVarChange: local!formSubmitRefresh
  ),
  local!integrationResults: a!refreshVariable(
    value: rule!AS_CO_UT_initializeBlankLocalVariable(
      type: 'type!{urn:com:appian:types:AS:GSS}AS_GSS_ConsensusResponseAiSummary?list'
    ),
    refreshOnVarChange: {
      local!formSubmitRefresh,
      local!dismissBanner
    }
  ),
  a!cardLayout(
    style: rule!AS_GSS_BrandingValueByKey(
      brandingKey: "BackgroundColor",
