                  size: "MEDIUM",

                )
              }
            )
          ),
          a!sideBySideItem(
            width: "MINIMIZE",
            item: a!radioButtonField(
              labelPosition: "COLLAPSED",
              choiceLabels: {
                rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!bundle,
                  bundleKey: "lbl_SuggestCombinedResponseUsingAI"
                ),
                rule!AS_GAM_CO_I18N_UT_displayLabel(
                  bundle: ri!bundle,
                  bundleKey: "lbl_CombineManually"
                )
              },
              choiceValues: { 1, 2 },
              choiceLayout: "COMPACT",
              value: local!selectedMethodToCombineResponses,
              saveInto: local!selectedMethodToCombineResponses
            ),
            showWhen: cons!AS_GSS_TOGGLE_CONSENSUS_FORM_AI
          )
        }
      ),
      rule!AS_GSS_GRD_ConsensusResponses(
        gridLabel: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!bundle,
          bundleKey: "acs_GroupedResponses",
          arguments: ri!responseTypeMap.responseTypeLabel
        ),
        showEditIcon: false,
        gridLabelPosition: "COLLAPSED",
        bundle: ri!bundle,
        shadeAlternateRows: false,
        showWhen: true,
        evaluationTasks: ri!evaluationTasks,
        consensusResponsesToDisplay: ri!consensusResponses[local!indexesToDisplay],
        allConsensusResponses: ri!consensusResponses,
        evaluationResponses: ri!evaluationResponses,
        responseTypeMap: ri!responseTypeMap,
        ratings: ri!ratings,
        selectable: true,
        selectedConsensusResponses: local!selectedConsensusResponses,
        pageSize: 5,
        evaluatorNickNames: ri!evaluatorNickNames,
        evaluation: ri!evaluation
      ),
      rule!AS_GSS_CRD_customisableBanner(
        showWhen: or(
          not(local!dismissBanner),
          not(
            and(local!integrationResults.isSuccess)
          )
        ),
        primaryIcon: "exclamation-triangle",
        primaryIconColor: rule!AS_GSS_BrandingValueByKey(
          brandingKey: "ConsensusCombinationAiErrorBannerPrimaryColor",
          useSuiteBranding: true
        ),
        /*#9F0019*/
        primaryText: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!bundle,
          bundleKey: "txt_ConsensusCombinationAiErrorMessage"
        ),
        primaryTextColor: rule!AS_GSS_BrandingValueByKey(
          brandingKey: "ConsensusCombinationAiErrorBannerPrimaryColor",
          useSuiteBranding: true
        ),
        /*#9F0019*/
        showDismissIcon: true,
        dismissIconSaveInto: a!save(local!dismissBanner, true),
        dismissIconColor: rule!AS_GSS_BrandingValueByKey(
          brandingKey: "ConsensusCombinationAiErrorBannerPrimaryColor",
          useSuiteBranding: true
        ),
        /*#9F0019*/
        dismissIconSize: "MEDIUM",
        decorativeBarColor: rule!AS_GSS_BrandingValueByKey(
          brandingKey: "ConsensusCombinationAiErrorBannerDecorativeBarColor",
          useSuiteBranding: true
        ),
        /*#DE0037*/
        dismissIconAltText: rule!AS_GAM_CO_I18N_UT_displayLabel(
          bundle: ri!bundle,
          bundleKey: "acs_RemoveMessage"
        ),
        cardLayoutStyle: rule!AS_GSS_BrandingValueByKey(
          brandingKey: "ConsensusCombinationAiErrorBannerCardStyle",
          useSuiteBranding: true
        ),
        /*#FFD8D8*/
        showBorder: false,
        marginAbove: "STANDARD",
        marginBelow: "STANDARD"
      ),
      /*COMBINE BUTTONS*/
      a!sectionLayout(
        contents: {
          a!sideBySideLayout(
            items: {
              a!sideBySideItem(
                item: a!ButtonArrayLayout(
                  buttons: {
                    a!buttonWidget(
                      label: rule!AS_GAM_CO_I18N_UT_displayLabel(
                        bundle: ri!bundle,
                        bundleKey: "btn_Cancel"
                      ),
                      style: "LINK",
                      accessibilityText: ri!responseTypeMap.responseTypeLabelSingle,
                      saveInto: {
                        a!save(ri!combinedConsensusResponse, null),
                        a!save(local!formSubmitRefresh, now())
                      }
                    )
                  },
                  align: "START"
                )
              ),
              a!sideBySideItem(
                item: a!ButtonArrayLayout(
                  buttons: {
                    a!buttonWidget(
                      label: rule!AS_GAM_CO_I18N_UT_displayLabel(
                        bundle: ri!bundle,
                        bundleKey: a!match(
                          value: null,
                          whenTrue: isnull(
                            ri!combinedConsensusResponse.consensusResponseId
                          ),
                          then: "btn_CombineX",
                          whenTrue: length(local!selectedConsensusResponseIndexes) = 0,
                          then: "btn_Separate",
                          default: "btn_UpdateX"
                        ),
                        arguments: count(local!selectedConsensusResponses)
                      ),
                      style: "SOLID",
                      color: "ACCENT",
                      loadingIndicator: true,
                      disabled: not(local!enableCombine),
                      accessibilityText: ri!responseTypeMap.responseTypeLabelSingle,
                      tooltip: if(
                        and(
                          local!enableCombine,
                          /*If selected method was Suggest combined response using AI*/
                          local!selectedMethodToCombineResponses = 1
                        ),
                        rule!AS_GAM_CO_I18N_UT_displayLabel(
                          bundle: ri!bundle,
                          bundleKey: "hlp_SuggestedResponseGeneration"
                        ),
                        null()
                      ),
                      saveInto: a!flatten(
                        {
                          if(
                            a!isNullOrEmpty(local!selectedConsensusResponseIndexes),
                            /* Handle separation */
                            {
                              if(
                                /* <= 0 means not yet saved */
                                ri!combinedConsensusResponse.consensusResponseId <= 0,
                                a!save(
                                  ri!consensusResponses,
                                  remove(
                                    ri!consensusResponses,
                                    local!indexOfGroupedConsensusResponse
                                  )
                                ),
                                a!save(
                                  ri!consensusResponses[local!indexOfGroupedConsensusResponse].isActive,
                                  false
                                )
                              ),
                              /* Handle deselected responses */
                              if(
                                a!isNotNullOrEmpty(local!deselectedResponseIndexes),
                                a!save(
                                  ri!consensusResponses[local!deselectedResponseIndexes].combinedConsensusResponseId,
                                  null
                                ),
                                {}
                              ),
                              /* Exit */
                              a!save(local!formSubmitRefresh, now()),
                              a!save(ri!combinedConsensusResponse, null)
                            },
                            /* Handle combination */
                            {
                              if(
                                /*If selected method was Suggest combined response using AI*/
                                local!selectedMethodToCombineResponses = 1,
                                {
                                  a!save(local!dismissBanner, false),
                                  rule!AS_GSS_SAVES_executeConsensusAIIntegration_CombineResponses(
                                    combinedConsensusResponse: ri!combinedConsensusResponse,
                                    selectedConsensusResponses: local!selectedConsensusResponses,
                                    consensusResponseAiIntegrationResults: local!integrationResults
                                  )
                                },
                                {
                                  /* manual combination of responses */
                                  a!save(
                                    ri!combinedConsensusResponse.response,
                                    joinarray(
                                      local!selectedConsensusResponses.response,
                                      char(10)
                                    )
                                  ),
                                  a!save(
                                    ri!combinedConsensusResponse.justification,
                                    joinarray(
                                      local!selectedConsensusResponses.justification,
                                      char(10)
                                    )
                                  ),
                                  a!save(
                                    ri!combinedConsensusResponse.isOriginallyGeneratedByAi,
                                    false
                                  ),
                                  if(
                                    cons!AS_GSS_TOGGLE_CONSENSUS_FORM_AI,
                                    rule!AS_GSS_MTR_SAVE_combinedConsensusResponsesWithoutConsensusAI(),
                                    {}
                                  )
                                }
                              ),
                              if(
                                not(
                                  and(local!integrationResults.isSuccess)
                                ),
                                null,
                                {
                                  /* References are combined manually in both cases */
                                  a!save(
                                    ri!combinedConsensusResponse.references,
                                    joinarray(
                                      union(
                                        local!selectedConsensusResponses.references,
                                        touniformstring({})
                                      ),
                                      char(10)
                                    )
                                  ),
                                  /* Handle new ccombined response */
                                  if(
                                    isnull(
                                      ri!combinedConsensusResponse.consensusResponseId
                                    ),
                                    {
                                      a!save(
                                        ri!combinedConsensusResponse.consensusResponseId,
                                        min(
                                          0,
                                          ri!consensusResponses.consensusResponseId
                                        ) - 1
                                      ),
                                      a!save(
                                        ri!consensusResponses,
                                        append(
                                          ri!consensusResponses,
                                          ri!combinedConsensusResponse
                                        )
                                      )
                                    },
                                    a!save(
                                      ri!consensusResponses[local!indexOfGroupedConsensusResponse],
                                      ri!combinedConsensusResponse
                                    )
                                  ),
                                  /* Handle selected responses */
                                  a!save(
                                    ri!consensusResponses[local!selectedConsensusResponseIndexes].combinedConsensusResponseId,
                                    ri!combinedConsensusResponse.consensusResponseId
                                  ),
                                  /* Handle deselected responses */
                                  if(
                                    a!isNotNullOrEmpty(local!deselectedResponseIndexes),
                                    a!save(
                                      ri!consensusResponses[local!deselectedResponseIndexes].combinedConsensusResponseId,
                                      null
                                    ),
                                    {}
                                  ),
                                  /*
                                    These variables will not update if two new combinations are created in a row
                                    or if the same combination is updated twice in a row without change
                                    because this form is hidden before the locals refresh
                                    and then the referenced vars match on reappearance
                                  */
                                  a!save(local!formSubmitRefresh, now()),
                                  /* Exit */
                                  a!save(ri!combinedConsensusResponse, null)
                                }
                              )
                            }
                          )
                        }
                      )
                    )
                  },
                  align: "END"
                )
              )
            },
            marginBelow: "NONE"
          )
        },
        divider: "ABOVE",
        marginAbove: "NONE",
        marginBelow: "NONE"
      )
    }
  )
)

